# Tutorial said to add these.from django.http import HttpResponse, JsonResponsefrom django.views.decorators.csrf import csrf_exemptfrom rest_framework.renderers import JSONRendererfrom rest_framework.parsers import JSONParser# Endfrom rest_framework import viewsetsfrom teams.models import Teamfrom teams.serializers import TeamSerializerclass TeamViewSet(viewsets.ModelViewSet):    queryset = Team.objects.all()    serializer_class = TeamSerializer# More shit the tutorial is telling me to add# "The root of our API is going to be a view that supports#  listing all the existing teams, or creating a new team"@csrf_exemptdef team_list(request):    """    List all teams, or create new team    """    if request.method == 'GET':        teams = Team.objects.all()        serializer = TeamSerializer(teams, many=True)        return JsonResponse(serializer.name, safe=False)